import React, { Component } from "react";
// import { bindActionCreators } from "redux";
// import { connect } from "react-redux";
// import * as categoryActions from "../redux/actions/categoriesAction";

class SearchComponent extends Component {
     state = {
       baseUrl: "https://api.unsplash.com/search/photos/?client_id",
       accessKey:"age-dmYcxYinzenv4oHzL4f9_NJWqU3otOzhdlhAQYA",
       query:"&query",
       inputValue:"",
       searchUrl:"",
       images:[]

     }
     handleChange =(event)=>{
       this.setState({[event.target.id]: event.target.value})
     }

     handleSubmit=(event)=>{
       event.preventDefault()
       this.setState({
          searchUrl: this.state.baseUrl+this.state.accessKey+this.state.query+this.state.inputValue
       },

       ()=>{
         fetch(this.state.searchUrl)
         .then(response => response.json())
         .then(json=> {
           this.setState({
            images:[json], 
            inputValue:""
           })
         })
       }
       )
     }


  componentDidMount() {

  }



  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
        <input
        id="images"

        value={this.state.inputValue}
        onChange={this.handleChange}/>

        <input type="submit"
        value="search for image"/>

        </form>
{this.state.images}

      </div>
    );
  }
}




export default(SearchComponent);

