import React, { Component } from "react";
// import { bindActionCreators } from "redux";
// import { connect } from "react-redux";
// import * as categoryActions from "../redux/actions/categoriesAction";
import { FormGroup } from "reactstrap";
import '../App.css';
class SearchComponent extends Component {
    state = {
        baseUrl: "https://api.unsplash.com/search/photos/?per_page=1&client_id=",
        accessKey: "age-dmYcxYinzenv4oHzL4f9_NJWqU3otOzhdlhAQYA",
        query: "&query=",
        inputValue: "",
        searchUrl: "",
        images: [],


    };

    // componentDidMount() {
    //   console.log("componentdidmount is running");
    //   this.getImages();
    // }


    handleChange = (event) => {
        console.log("handlechange running");
        this.setState({ inputValue: event.target.value }, () => {

        });

        const tempSearchURL =
            this.state.baseUrl +
            this.state.accessKey +
            this.state.query +
            event.target.value;

        this.setState({ searchUrl: tempSearchURL }, () => {
            //console.log(this.state.searchUrl);

        });
    };

    handleSubmit = (event) => {
        //console.log(this.state.inputValue);
        this.getImages();
        event.preventDefault();
    };

    handleReset = () => {
        this.setState({
            inputValue: "",
            searchUrl: "",
            images: [],
        })
        console.log("reset")
    }

    getImages = () => {
        //console.log("getimages is running");
        fetch(this.state.searchUrl)
            .then((response) => response.json())
            .then((data) => {
                //console.log(data.results[0].urls.small)
                const imgs = this.state.images.concat(data.results);
                console.log(imgs);
                this.setState({ images: imgs }, () => {
                    console.log(this.state.images);

                });
            });
    };

    render() {
        return ( <
            div className = "searchComp" >
            <
            form onSubmit = { this.handleSubmit }
            class = "form-group" >
            <
            FormGroup >
            <
            div class = "input-group mb-3" >
            <
            input type = "text"
            class = "form-control"
            placeholder = "Enter Valid Input"
            value = { this.state.inputValue }
            onChange = { this.handleChange }
            /> <
            div class = "input-group-append" >
            <
            button class = "btn btn-warning"
            type = "submit" >
            Search <
            /button> <
            /div> <
            /div> <
            /FormGroup>

            <
            button onClick = { this.handleReset }
            class = "btn btn-success" > reset < /button> <
            /form> { /* <button onSubmit={this.handleReset}>reset</button> */ } <
            ul className = "displayImg" > {
                this.state.images.map((item, i) => ( <
                    h1 key = { i } >
                    <
                    img src = { item.urls.small } > < /img> <
                    /h1>
                ))
            } <
            /ul> { /* <h2><span>description:</span> {this.state.images[0].alt_description}</h2> */ } <
            /div>
        );
    }
}

export default SearchComponent;

