import React, { Component } from "react";
// import { bindActionCreators } from "redux";
// import { connect } from "react-redux";
// import * as categoryActions from "../redux/actions/categoriesAction";

class SearchComponent extends Component {
  state = {
    baseUrl: "https://api.unsplash.com/search/photos/?client_id=",
    accessKey: "age-dmYcxYinzenv4oHzL4f9_NJWqU3otOzhdlhAQYA",
    query: "&query=",
    inputValue: "",
    searchUrl: "",
    images: [],
  };

  componentDidMount() {
    console.log("componentdidmount is running");
    //this.getImages();
  }
  handleChange = (event) => {
    console.log("handlechange running");
    this.setState({ inputValue: event.target.value });

    // this.setState({
    //   searchUrl:
    //     this.state.baseUrl +
    //     this.state.accessKey +
    //     this.state.query +
    //     this.state.inputValue,
    // });
  };

  handleSubmit = (event) => {
    console.log(this.state.inputValue);
    // this.getImages();
    event.preventDefault();
    const tempSearchURL =
      this.state.baseUrl +
      this.state.accessKey +
      this.state.query +
      this.state.inputValue;
    this.setState({ searchUrl: tempSearchURL });
    console.log(`tempsearch: ${tempSearchURL}`);
    console.log("this.state.searchURL: " + this.state.searchUrl);
    console.log(
      "kombin:" +
        this.state.baseUrl +
        this.state.accessKey +
        this.state.query +
        this.state.inputValue
    );
  };

  getImages = () => {
    console.log(this.state.searchUrl);
    fetch(this.state.searchUrl)
      .then((response) => response.json())
      .then((data) => {
        this.setState({ images: [data] });
      });
  };

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            id="search_box"
            type="text"
            value={this.state.inputValue}
            onChange={this.handleChange}
          />

          <input type="submit" />
        </form>
        {this.state.images}
      </div>
    );
  }
}

export default SearchComponent;

